BOOKTYPE_CSS_BOOK = '.objavi-chapter{ \n  color: #000; \n} \n\na { \n  text-decoration:none; \n  color:#000; \n} \n\nh1 .initial{ \n  color: #000; \n} \n\n.objavi-subsection{ \n  display: block; \n  page-break-before: always; \n/* page-break-after: always;*/ \n  text-transform: uppercase; \n  font-size: 20pt; \n} \n\nbody .objavi-subsection:first-child{ \n  page-break-before: avoid; \n} \n\n\n.objavi-subsection .initial { \n  font-size: 1em; \n  color: #000; \n} \n\n.objavi-subsection-heading { \n  font-size: 20pt; \n  text-align: center; \n  line-height: 300px; \n  font-weight: normal; \n} \n\n\nh1 { \n  page-break-before: always; \n} \n\n\ntable { \n  float: none; \n} \n\nh1.frontpage{ \n  page-break-after:always; \n  margin-top:70%; \n  font-size: 20pt; \n  text-align: center; \n  page-break-before: avoid; \n  font-weight: normal; \n} \n\ndiv.copyright{ \n  padding: 1em; \n} \n/* TOC ******************************/ \ntable { \n  float: none; \n} \n\ntable.toc { \n  font-size: 1.1em; \n  width: 95%; \n} \n\ntable.toc td{ \n  vertical-align:top \n  padding-left: 0.5em; \n} \n\ntd.chapter { \n  padding: 0 0.5em; \n  text-align: right; \n} \n\ntable.toc td.pagenumber { \n  text-align: right; \n  vertical-align:bottom; \n} \n\ntd.section { \n  padding-top: 1.1em; \n  font-weight: bold; \n} \n/* End TOC **************************/ \n\n\npre { \n  overflow: hidden; \n  white-space: pre-wrap; \n} \n\n\nh1, h2, h3, h4, h5, h6{ \n  page-break-after: avoid; \n  page-break-inside: avoid; \n} \n\n\n.page-break{ \n  page-break-before: always; \n  height: 7em; \n  display: block; \n} \n\n\n#right-footer { \n  text-align: right; \n} \n\n#left-footer { \n  text-align: left; \n} \n\na { \n  word-wrap: break-word; \n} \n\n.objavi-no-page-break { \n  page-break-inside: avoid; \n} \n\n.unseen{ \n  z-index: -66; \n  margin-left: -1000pt; \n}\n\nsup {\n  vertical-align:text-top;\n  font-size:0.7em; \n}\n\nimg { max-width: 95%; }\n\np { word-wrap: break-word; }\n\nli { word-wrap: break-word; }\n \t '

BOOKTYPE_CSS_BOOKJS = 'body {\n font-size: 12px;\n line-height: 20px;\n background-color: rgb(180, 180, 180);\n padding: 0;\n margin: 0;\n font-family:  Verdana, Geneva, sans-serif;\n -webkit-print-color-adjust:exact;\n  word-wrap: break-word;  \n  font-weight:normal !important;\n  widows:2;\n  orphans:2;\n}\n\n/* PARAGRAPHS */\np {\n font-size:12px;\n line-height:20px;\n word-wrap: break-word; \n}\n\n\n/* HEADINGS*/\nh1 {\n font-size:21px;\n font-weight:heavy !important;\n color: #cf2f19;\n}\n\nh2 {\n font-size:15px;\n color: black!important;\n}\n\nh3 {\n font-size:14px;\n font-weight:heavy !important;\n}\n\n\n/* LINKS*/\na { \n word-wrap:break-word; \n}\n\na:-webkit-any-link{\n text-decoration:none !important;\n color: black!important;\n}\n\n/* IMAGES */\nimg {\n padding-bottom:5px;\n max-width:90%;\n height: auto;\n}\n\n\n/*CODE BLOCKS*/\npre {\n white-space: pre-wrap;       /* css-3 */\n white-space: -moz-pre-wrap;  /* Mozilla, since 1999 */\n white-space: -pre-wrap;      /* Opera 4-6 */\n white-space: -o-pre-wrap;    /* Opera 7 */\n word-wrap: break-word;       /* Internet Explorer 5.5+ */\n font-family: courier !important;\n font-size:0.8em;\n  widows:4;\n  orphans:4;\n}\n\ncode {\n font-family: courier !important;\n font-size:0.9em;\n}\n\n/*LISTS*/\nli { \n font-size:12px !important;\n word-wrap:break-word;\n font-family: verdana !important;\n page-break-inside:avoid; \n  widows:4;\n  orphans:4;\n}\n\n\n/* SECTION PAGE */\nh1.sectiontitle {\n font-size:28px;\n font-weight:normal !important;\n}\n\nh2.sectionchaptertitle {\n font-size:21px;\n font-weight:normal !important;\n margin-left:20px;\n}\n\n\n/* TITLEPAGE */\n#booktitle {\n margin-top:80mm;\n font-size:20px;\n font-weight:700;\n text-align:center;\n margin-left:auto:\n margin-right:auto;\n}\n\n#booksubtitle {\n font-size:18px;\n margin-top:6mm;\n font-weight:700;\n text-align:center;\n margin-left:auto:\n margin-right:auto;\n}\n\n#bookeditors {\n padding-top:50mm;\n font-weight:700;\n text-align:center;\n font-size:12px;\n}\n\n/*GLOSSARY*/\n\ndt {\n  page-break-after:avoid;\n}\n\n/*NOTES*/\n#note\n{\n border: 1px solid;\n margin: 10px 0px;\n padding:15px 10px 15px 10px;\n -moz-border-radius: 15px;\n -webkit-border-radius: 15px;\n border-radius: 15px;\n max-width:60% !important:\n}\n\n#note p\n{\n max-width:60% !important:\n}\n\n\n/* TABLES GENERAL*/\n\ntable { \n word-wrap: break-word;\n page-break-inside:avoid;\n font-family: verdana !important;\n}\ntd {\n vertical-align:top;\n padding:2mm;\n font-family: verdana !important;\n  font-size:0.9em;\n}\n\n\ntable img {\n max-width:100% !important;\n height:auto;\n}\n\ntable a {\n word-wrap:break-word;\n font-size:0.8em ;\n}\n\n\n/* TABLES SPECIAL*/\n#hor-minimalist-b\n{\n font-family: sans-serif;\n background: #fff;\n border-collapse: collapse;\n text-align: left;\n}\n#hor-minimalist-b th\n{\n font-size: 12px !important;\n font-weight: normal;\n padding: 10px 8px;\n border-bottom: 2px solid #6678b1;\n}\n#hor-minimalist-b td\n{\n border-bottom: 1px solid #ccc;\n font-size: 12px !important;\n padding: 6px 8px;\n}\n\n#hor-minimalist-b td p\n{\n font-size: 12px !important;\n line-height:1.2em; \n}\n\n\n/* SPECIAL DIV */\n.no-page-break {\n page-break-inside:avoid;\n padding-top:1px;\n}\n\n\n/* HEADERS */\n\n/* Do not display section titles in the header on odd pages */\n.pagination-page:nth-child(odd) .pagination-header .pagination-section {\ndisplay:none;\n}\n\n/* Do not display chapter titles in the header on even pages */\n.pagination-page:nth-child(even) .pagination-header .pagination-chapter {\ndisplay:none;\n}\n\n\n/* ELEMENTS OF THE TABLE OF CONTENTS */\n\n/* The title text of the TOC */\n#pagination-toc-title:before {\ncontent:"Contents"; /* SET AUTOMATICALLY */\n}\n\n/* Styling of the TOC title */\n#pagination-toc-title {\nfont-size:22px;\nfont-weight:700;\ntext-align:left;\npadding-bottom:.4in;\n}\n\n/* Styling of individual toc entry */\n.pagination-toc-entry {\nwidth:4.2in;\ndisplay:block;\n}\n\n/* Page numbers of toc entries should float to the right and have normal font weight */\n.pagination-toc-entry .pagination-toc-pagenumber {\nfont-weight:400;\ndisplay:inline-block;\nvertical-align:text-bottom;\nfont-size:16px;\nfloat:right; /* SET AUTOMATICALLY */\n}\n\n/* special styling for TOC entries that represent sections */\n.pagination-toc-entry.section {\nfont-weight:700;\nfont-size:16px;\npadding-top:.2in;\n}\n\n'

BOOKTYPE_CSS_EBOOK = '.objavi-chapter{\n  color: #000;\n  display:none;\n} \n\na {\n  text-decoration:none;\n  color:#000;\n} \n\nh1 .initial{\n  color: #000;\n  display:none;\n} \n\n.objavi-subsection{\n  display: block;\n  page-break-before: always;\n} \n\nbody .objavi-subsection:first-child{\n  page-break-before: avoid;\n} \n\n.objavi-subsection .initial {\n  color: #000;\n  display:none;\n} \n\n.objavi-subsection-heading {\n  font-size: 20pt;\n  text-align: center;\n  line-height: 300px;\n  font-weight: normal;\n} \n\ntable {\n  float: none;\n} \n\nh1.frontpage{\n  page-break-after:always;\n  margin-top:70%;\n  font-size: 20pt;\n  text-align: center;\n  page-break-before: avoid;\n  max-width: 700pt;\n  font-weight: normal;\n} \n\ndiv.copyright{\n  padding: 1em;\n}\n/* TOC ******************************/\ntable {\n  float: none;\n} \n\ntable.toc {\n  font-size: 1.1em;\n  width: 95%;\n} \n\ntable.toc td {\n  vertical-align:top\n  padding-left: 0.5em;\n} \n\ntd.chapter {\n  padding: 0 0.5em;\n  text-align: right;\n} \n\ntable.toc td.pagenumber {\n  text-align: right;\n  vertical-align:bottom;\n} \n\ntd.section {\n  padding-top: 1.1em;\n  font-weight: bold;\n}\n\t\t\t\t\t\n/* End TOC **************************/ \nimg {\n   max-width: 500px;\n   height: auto;\n} \n\n.objavi-no-page-break {\n   page-break-inside: avoid;\n} \n\n.unseen {\n   z-index: -66;\n   margin-left: -1000pt;\n} \n\n.objavi-subsection-heading{\n  height:860px;\n  font-size:0px;\n  display:block;\n}\n'

